2fa7a70e8988872d5ca975577467cc2612b4f3e1.source:
query AllPokemon($offset: Int = 89, $take: Int) {
  getAllPokemon(offset: $offset, take: $take) {
    __typename
    ...LightDataFragmentWithoutNested
  }
}
fragment LightDataFragmentWithoutNested on Pokemon {
  __typename
  key
  num
  species
  sprite
  color
  types {
    __typename
    ...PokemonLightTypeFragment
  }
}
fragment PokemonLightTypeFragment on PokemonType {
  __typename
  name
}

a67810f5c4c39f3c6d246d3aa857071069d04cff.source:
query GetPokemon($pokemon: PokemonEnum!, $offsetFlavorTexts: Int, $takeFlavorTexts: Int, $reverseFlavorTexts: Boolean) {
  getPokemon(
    pokemon: $pokemon
    offsetFlavorTexts: $offsetFlavorTexts
    takeFlavorTexts: $takeFlavorTexts
    reverseFlavorTexts: $reverseFlavorTexts
  ) {
    __typename
    ...FullData
  }
}
fragment FullData on Pokemon {
  __typename
  ...FullDataFragment
  evolutions {
    __typename
    ...FullDataFragment
    evolutions {
      __typename
      ...FullDataFragment
    }
    preevolutions {
      __typename
      ...FullDataFragment
    }
  }
  preevolutions {
    __typename
    ...FullDataFragment
    evolutions {
      __typename
      ...FullDataFragment
    }
    preevolutions {
      __typename
      ...FullDataFragment
    }
  }
}
fragment FullDataFragment on Pokemon {
  __typename
  ...FullDataFragmentWithoutNested
}
fragment FullDataFragmentWithoutNested on Pokemon {
  __typename
  key
  legendary
  bulbapediaPage
  evYields {
    __typename
    ...EvYieldsFragment
  }
  evolutionLevel
  forme
  formeLetter
  gender {
    __typename
    ...GenderFragment
  }
  height
  isEggObtainable
  backSprite
  num
  shinyBackSprite
  shinySprite
  species
  sprite
  types {
    __typename
    ...PokemonTypeFragment
  }
  baseStats {
    __typename
    ...StatsFragment
  }
  baseStatsTotal
  color
  weight
}
fragment EvYieldsFragment on EvYields {
  __typename
  hp
  attack
  defense
  specialattack
  specialdefense
  speed
}
fragment GenderFragment on Gender {
  __typename
  female
  male
}
fragment PokemonTypeFragment on PokemonType {
  __typename
  name
  matchup {
    __typename
    ...TypeMatchupFragment
  }
}
fragment TypeMatchupFragment on TypeMatchup {
  __typename
  attacking {
    __typename
    ...TypeEffectivenessFragment
  }
  defending {
    __typename
    ...TypeEffectivenessFragment
  }
}
fragment TypeEffectivenessFragment on TypeEffectiveness {
  __typename
  doubleEffectiveTypes
  doubleResistedTypes
  effectiveTypes
  effectlessTypes
  normalTypes
  resistedTypes
}
fragment StatsFragment on Stats {
  __typename
  hp
  attack
  defense
  specialattack
  specialdefense
  speed
}
