5a3a2598869ecb24b4d9bf6b42b3bdcbac50e1e2.source:
query AllPokemon($offset: Int = 89) {
  getAllPokemon(offset: $offset) {
    __typename
    ...LightDataFragmentWithoutNested
  }
}
fragment LightDataFragmentWithoutNested on Pokemon {
  __typename
  key
  num
  species
  sprite
  types {
    __typename
    ...PokemonLightTypeFragment
  }
}
fragment PokemonLightTypeFragment on PokemonType {
  __typename
  name
}

91d594d10ee5c40e56879e884a4cc8c92c3602c5.source:
query GetPokemon($pokemon: PokemonEnum!, $offsetFlavorTexts: Int, $takeFlavorTexts: Int, $reverseFlavorTexts: Boolean) {
  getPokemon(
    pokemon: $pokemon
    offsetFlavorTexts: $offsetFlavorTexts
    takeFlavorTexts: $takeFlavorTexts
    reverseFlavorTexts: $reverseFlavorTexts
  ) {
    __typename
    ...FullData
  }
}
fragment FullData on Pokemon {
  __typename
  ...FullDataFragment
  evolutions {
    __typename
    ...FullDataFragment
    evolutions {
      __typename
      ...FullDataFragment
    }
    preevolutions {
      __typename
      ...FullDataFragment
    }
  }
  preevolutions {
    __typename
    ...FullDataFragment
    evolutions {
      __typename
      ...FullDataFragment
    }
    preevolutions {
      __typename
      ...FullDataFragment
    }
  }
}
fragment FullDataFragment on Pokemon {
  __typename
  ...FullDataFragmentWithoutNested
}
fragment FullDataFragmentWithoutNested on Pokemon {
  __typename
  key
  legendary
  bulbapediaPage
  evYields {
    __typename
    ...EvYieldsFragment
  }
  evolutionLevel
  forme
  formeLetter
  gender {
    __typename
    ...GenderFragment
  }
  height
  isEggObtainable
  backSprite
  num
  shinyBackSprite
  shinySprite
  species
  sprite
  types {
    __typename
    ...PokemonTypeFragment
  }
  baseStats {
    __typename
    ...StatsFragment
  }
  baseStatsTotal
  color
  weight
}
fragment EvYieldsFragment on EvYields {
  __typename
  hp
  attack
  defense
  specialattack
  specialdefense
  speed
}
fragment GenderFragment on Gender {
  __typename
  female
  male
}
fragment PokemonTypeFragment on PokemonType {
  __typename
  name
  matchup {
    __typename
    attacking {
      __typename
      doubleEffectiveTypes
      doubleResistedTypes
      effectiveTypes
      effectlessTypes
      normalTypes
      resistedTypes
    }
    defending {
      __typename
      doubleEffectiveTypes
      doubleResistedTypes
      effectiveTypes
      effectlessTypes
      normalTypes
      resistedTypes
    }
  }
}
fragment StatsFragment on Stats {
  __typename
  hp
  attack
  defense
  specialattack
  specialdefense
  speed
}
